const int bitmapSize = sizeof(bitmap) / sizeof(bitmap[0]);

// Pixel masks:
// GREEN  - %00 for background color 0 ($d021)
// WHITE  - %01 for the upper nibble of the screen matrix
// PURPLE - %10 for lower nibble of the screen matrix
// BLACK  - %11 for the lower nibble of color RAM
const unsigned char pattern[4] = {0,3,1,2};
// At 2 bpp, shift the 2 pixeld into place
const unsigned char shift[4] = {6,4,2,0};

void dump(const char *filename, unsigned char* bitmap, const int size)
{
    FILE *fp;
    int written, i,j,k;
    unsigned char* data, *data1, c;

    // First buffer for bitmap to pixels
    data = (unsigned char*)malloc(bitmapSize/4);
    // Second buffer for screen layout
    data1 = (unsigned char*)malloc(bitmapSize/4);
    memset(data,0,bitmapSize/4);
    memset(data1,0,bitmapSize/4);
    if(!data || !data1)
    {
        printf("Could not allocate %d bytes for data and/or data1\n",bitmapSize/4);
        return;
    }

    // Turn indices into pixels in visual order
    k = 0;
    i = 0;
    while(bitmap[i])
    {
        for(j=0;j<4;j++)
        {
            data[k] |= (pattern[bitmap[i+j]-'0'] << shift[j]);
        }
        k++;
        i += 4;
    }

    // Move pixels into the C64 screen format:
    // 0  8
    // 1  9
    // 2 10
    // 3 etc. where 320 is visible on-screen below 7
    // 4
    // 5
    // 6
    // 7
    for(i=0;i<200;i++)
    {
        for(j=0;j<40;j++)
        {
            data1[(i/8)*320+(i%8)+j*8] = data[i*40+j];
        }
    }

    // Save
    if(!(fp = fopen(filename, "wb")))
    {
        printf("Could not open %s for writing\n", filename);
        return;
    }
    written = fwrite(data1, 1, k, fp);
    if(written != k)
        printf("On file %s should write %d but wrote %d\n", filename, k, written);
    else
        printf("Wrote %d bytes to %s\n",k,filename);

    fclose(fp);
    free(data);
    free(data1);
};

int main()
{
    dump("bitmap.bin", bitmap, bitmapSize);
 
    return 0;
}
